    <script>
        $(document).ready(function() {
            // Find the Ukraine path by its class and add highlight class
            const ukrainePath = $('path.UA');
            
            // Set initial styles
            ukrainePath.css({
                'fill': '#FFA500',
                'stroke': '#FFA500',
                'stroke-width': '2px',
                'cursor': 'pointer',
                'transition': 'all 0.3s ease'
            });

            // Add hover effects
            ukrainePath.hover(
                function() {
                    $(this).css({
                        'fill': '#FF8C00',
                        'stroke': '#FF8C00',
                        'stroke-width': '3px'
                    });
                },
                function() {
                    $(this).css({
                        'fill': '#FFA500',
                        'stroke': '#FFA500',
                        'stroke-width': '2px'
                    });
                }
            );
        });
    </script>
    <style>
        /* Make all other countries darker gray */
        path:not(.UA) {
            fill: #A0A0A0 !important;
            stroke: #808080 !important;
            stroke-width: 1px !important;
        }
    </style>
